import React,{useState} from 'react'

const Signup = () => {
  const [credentials, setcredentials] = useState({email:"", password:""})

    const handlesubmit = async (e)=>{
        e.preventDefault();
        const response = await fetch(`http://localhost:5000/api/auth/login`, {
            method: 'POST', 
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({email:credentials.email, password: credentials.password}) 
          });
          const json = await response.json()
          console.log(json)
          if(json.success){

          }
          else{
            alert("Invalid Credentials")
          }
    }
    const onChange = (e)=>{
      setcredentials({...credentials, [e.target.name]:e.target.value})
    }

  return (
    <div>
       <form onSubmit={handlesubmit}>
       <div className="mb-3">
    <label htmlFor="name" className="form-label">Name</label>
    <input type="text" className="form-control" id="name" value="" onChange={onChange} aria-describedby="emailHelp"/>
  </div>
  <div className="mb-3">
    <label htmlFor="email" className="form-label">Email address</label>
    <input type="email" className="form-control" id="email" name="email" value={credentials.email} onChange={onChange} aria-describedby="emailHelp"/>
    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
  </div>

  <div className="mb-3">
    <label htmlFor="password" className="form-label">Password</label>
    <input type="password" className="form-control" id="password" value={credentials.password} onChange={onChange} name="password"/>
  </div>

  <div className="mb-3">
    <label htmlFor="cpassword" className="form-label">Confirm Password</label>
    <input type="cpassword" className="form-control" id="cpassword" value={credentials.password} onChange={onChange} name="password"/>
  </div>

  <button type="submit" className="btn btn-primary" >Submit</button>
</form>
    </div>
  )
}

export default Signup
