import React from 'react'
import noteContext from './noteContext'
import { useState } from 'react'

const NoteState = (props)=>{
  const host = "http://localhost:5000"
    const notesInitial = []

      const [notes, setNotes] = useState(notesInitial)

        //Get all note
        const getNotes = async()=>{
          // TODO : API CALL
          const response = await fetch(`${host}api/notes/fetchallnotes`, {
            method: 'GET', 
            headers: {
              'Content-Type': 'application/json',
              'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjMyZmYxNjE4YmE0YzU4OWY4OGFiMTlkIn0sImlhdCI6MTY2NDEwNDUwMH0.WuINcXXvUUXZpahXulPivh4kqdQeHIv12NRLoZ-cTW8'
            },
          });
          const json = await response.json()
          console.log(json)

        }

      //Add a note
      const addNote = async(title, description, tag)=>{
          // TODO : API CALL
          const response = await fetch(`${host}/api/notes/addnote`, {
            method: 'POST', 
            headers: {
              'Content-Type': 'application/json',
              'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjMyZmYxNjE4YmE0YzU4OWY4OGFiMTlkIn0sImlhdCI6MTY2NDEwNDUwMH0.WuINcXXvUUXZpahXulPivh4kqdQeHIv12NRLoZ-cTW8'
            },
            body: JSON.stringify({title, description, tag}) 
          });
          const json = response.json(); 

          console.log("Adding a new note")
          const note = {
            "_id": "6332fc54802c692ee7dee91f",
        "user": "632ff1618ba4c589f88ab19d",
        "title": title,
        "description": description,
        "tag": tag,
        "date": "2022-09-27T13:36:20.996Z",
        "__v": 0
          };
          setNotes(notes.concat(note))
      }

      //Delete a note
      const deleteNote = (id)=>{
        // TODO : API CALL
        console.log("Deleing a note with id" + id)
        const newNotes = notes.filter((note)=> {return note._id!==id})
         setNotes(newNotes);
      }

      //Edit a note
      const editNote = async (id, title, description, tag)=>{
        // TODO : API CALL

        const response = await fetch(`${host}/api/notes/updatenote/6330c92d0bfdedf4fc48eec7`, {
          method: 'POST', 
          headers: {
            'Content-Type': 'application/json',
            'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjMyZmYxNjE4YmE0YzU4OWY4OGFiMTlkIn0sImlhdCI6MTY2NDEwNDUwMH0.WuINcXXvUUXZpahXulPivh4kqdQeHIv12NRLoZ-cTW8'
          },
          body: JSON.stringify({title, description, tag}) 
        });
        const json = response.json(); 

        for(let index=0; index<notes.length; index++){
          const element = notes[index];
          if(element._id === id){
            element.title = title;
            element.description = description;
            element.tag = tag;
          }
        }
      }

    return(
        <noteContext.Provider value= {{notes, setNotes, addNote, deleteNote, editNote, getNotes}}>
            {props.children}
        </noteContext.Provider>
    )
    }
export default NoteState;
